import { Component } from '@angular/core';
import { IMenuCafe } from '../models/menuCafeInterface'
import { MenuCafe as mydata } from '../data/menuCafe'

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  Dishes: IMenuCafe[] = mydata;
}
<div class="container gap-2">
  <div class="row gap-2">
    <div class="col-2 gap-2">
      <app-listCafe *ngFor="let dish of Dishes; index as i" [Dishes]="Dishes[i]" (click)="ListCafe.ShowContent = !ShowContent;" [ngStyle]="{'background-color': ShowContent && Dishes.vegan ? 'rgb(155 235 156)' : '#CCC'}"></app-listCafe>
    </div>
    <div class="col gap-2">
      <app-menuCafe *ngFor="let dish of Dishes; index as i" [Dishes]="Dishes[i]" #menu></app-menuCafe>
    </div>
  </div>
</div>
