import { Component } from '@angular/core';
import { IMenuCafe } from '../models/menuCafeInterface'
import { MenuCafe as mydata } from '../data/menuCafe'

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  ShowContent = false;
  Dishes: IMenuCafe[] = mydata;
}
<div class="d-flex flex-column">
    <app-menuCafe *ngFor="let dish of Dishes; index as i" [Dishes]="Dishes[i]" (click)="ShowContent = true;"></app-menuCafe>
</div>
