import { Component } from '@angular/core';
import { IMenuCafe } from '../models/menuCafeInterface'
import { MenuCafe as mydata } from '../data/menuCafe'

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  Dishes: IMenuCafe[] = mydata;
}
<div class="grid gap-2" style="--bs-columns: 2;">
    <div class="g-col-2 gap-2">
      <app-listCafe *ngFor="let dish of Dishes; index as i" [Dishes]="Dishes[i]"></app-listCafe>
    </div>
    <div class="g-col-6 gap-2">
      <app-menuCafe *ngFor="let dish of Dishes; index as i" [Dishes]="Dishes[i]"></app-menuCafe>
    </div>
</div>
