import { Component, ViewChild } from '@angular/core';
import { IMenuCafe } from '../models/menuCafeInterface'
import { MenuCafe as mydata } from '../data/menuCafe'

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  Dishes: IMenuCafe[] = mydata;
  Dish:any = undefined;
  ShowContent = false;

  Show(show: boolean): void {
    this.ShowContent = !this.ShowContent;
    console.log(this.ShowContent)
  }
  ShowDish(dish: IMenuCafe): void {
    this.Dish = dish;
  }
}
<div class="container gap-2">
  <div class="row gap-2">
    <div class="col-2 gap-2">
      <app-listCafe *ngFor="let dish of Dishes; index as i" [Dishes]="Dishes[i]" #list (ShowContentEvent)="Show($event)" (DishEvent)="ShowDish($event)"></app-listCafe>
    </div>
    <div class="col gap-2">
      <app-menuCafe [ShowContent]="ShowContent" [Dishes]></app-menuCafe>
    </div>
  </div>
</div>
