import { Component, Input, Output } from '@angular/core'
import { IMenuCafe } from '../../models/menuCafeInterface'

@Component({
  selector: 'app-listCafe',
  templateUrl: './listCafe.component.html'
})
export class ListCafe {
  @Output()
  ShowContent = false;

  @Input()
  Dishes!: IMenuCafe

}

<div class="row d-flex flex-row gap-4 p-2 rounded" (click)="ShowContent = !ShowContent;" [ngStyle]="{'background-color': ShowContent && Dishes.vegan ? 'rgb(155 235 156)' : '#CCC'}">
<div class="d-flex flex-column">
  <span class="fs-5">{{ Dishes.name }}</span>
</div>
</div>
