import { Component, Input } from '@angular/core'
import { IMenuCafe } from '../../models/menuCafeInterface'

@Component({
  selector: 'app-menuCafe',
  templateUrl: './menuCafe.component.html'
})
export class MenuCafe {
  @Input()
  Dishes!: IMenuCafe
}

<div class="container d-flex flex-row gap-4" >
<img [src]="Dishes.image" [alt]="Dishes.name" width="150" height="150">
<div class="d-flex flex-column">
  <span class="fs-2">{{ Dishes.name }}</span>
  <span class="fs-4"> {{ Dishes.category.section }} </span>
  <span class="fs-6">{{ Dishes.price }} rub.</span>
</div>
</div>
